import React from 'react';

const ScoreBar = ({ score, setScore }) => {
    return (
        <div style={{ margin: '20px 0' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '10px' }}>
                <span>0</span>
                <input
                    type="range"
                    min="0"
                    max="10"
                    value={score}
                    onChange={(e) => setScore(parseInt(e.target.value))}
                    style={{
                        flex: 1,
                        height: '8px',
                        borderRadius: '5px',
                        background: `linear-gradient(to right, #87CEEB 0%, #1e3c72 ${score * 10}%, #ddd ${score * 10}%, #ddd 100%)`,
                        outline: 'none',
                        cursor: 'pointer'
                    }}
                />
                <span>10</span>
            </div>
            <div style={{ textAlign: 'center', fontSize: '1.2rem', fontWeight: 'bold', color: '#1e3c72' }}>
                Score: {score}/10
            </div>
        </div>
    );
};

export default ScoreBar;
